import React, { useState } from 'react';

const Home = () => {
  const [selectedMusic, setSelectedMusic] = useState('karnatic');

  const handleMusicSelection = (event) => {
    setSelectedMusic(event.target.value);
  };

  const startQuiz = () => {
    localStorage.setItem('selectedMusic', selectedMusic); // Store selection for other components
    navigate('/quiz');
  };

  return (
    <div className="home">
      <img src="/logo.png" alt="KBC Logo" style={{ width: '150px', marginBottom: '20px' }} />
      <h1 className="text-4xl font-bold">Welcome to KBC with Chocolates!</h1>
      <label className="block mt-4">
        Choose Background Music:
        <select
          value={selectedMusic}
          onChange={handleMusicSelection}
          className="border rounded px-2 py-1 mt-2"
        >
          <option value="karnatic">Karnatic</option>
          <option value="hindustani">Hindustani</option>
        </select>
      </label>
      <button
        onClick={startQuiz}
        className="bg-blue-500 text-white py-2 px-4 rounded mt-4"
      >
        Start Quiz
      </button>
    </div>
  );
};

export default Home;
