import React, { useEffect, useState } from 'react';
import '../styles/Quiz.css'; // Add a CSS file for styling

const Quiz = () => {
  const [questions, setQuestions] = useState([]);  // Questions without correct answer
  const [questionsWithAnswers, setQuestionsWithAnswers] = useState([]); // Questions with correct answer
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState([]);  // To store feedback for each question
  const [answerSubmitted, setAnswerSubmitted] = useState(false);  // Track if the answer was submitted for current question

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const selectedMusic = localStorage.getItem('selectedMusic');
        console.log("Selected Music >>>>> " + selectedMusic);
        const response = await fetch('http://localhost:5000/api/v1/questions/generate', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            topic: selectedMusic,
            difficulty: 'easy',
            questionsCount: 10,
          }),
        });
        const data = await response.json();
        const questionsArray = data.questions;
        setQuestionsWithAnswers(questionsArray);
        const forUser = questionsArray.map(({ correctAnswer, ...rest }) => rest);
        setQuestions(forUser);
      } catch (err) {
        console.error('Error fetching questions:', err);
      }
    };

    fetchQuestions();
  }, []);

  const handleSubmit = () => {
    const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer.trim().toUpperCase();
    const selectedLetter = selectedOption.split(')')[0].trim().toUpperCase(); // Extracting the letter and trimming spaces

    // Use regular expression to extract the letter from the correctAnswer string
    const correctLetter = correctAnswer.replace(/[^A-D]/g, '').toUpperCase();  // Extract only the letter (A, B, C, D)

    console.log(`Correct Letter: ${correctLetter},  Correct Answer: ${correctAnswer} Selected Option: ${selectedLetter}`);

    // Check if the correct answer matches the selected option letter (A, B, C, D)
    const isCorrect = correctLetter === selectedLetter;

    console.log("IsCorrect Answer >>>>> ", isCorrect);

    if (isCorrect) {
      setScore(score + 1);
    }

    // Store feedback for this question
    setFeedback([
      ...feedback,
      { questionIndex: currentIndex, isCorrect, selectedLetter }
    ]);

    setAnswerSubmitted(true);  // Mark that the answer has been submitted
  };

  const handleNextQuestion = () => {
    // Reset for the next question
    setSelectedOption(null);
    setAnswerSubmitted(false); // Reset the submission flag
    setCurrentIndex(currentIndex + 1);
  };

  if (questions.length === 0) {
    return <div className="loading">Loading questions...</div>;
  }

  if (currentIndex >= questions.length) {
    return (
      <div className="final-score">
        <div>Your final score is: {score} / {questions.length}</div>
        <h4>Feedback:</h4>
        <ul>
          {feedback.map(({ questionIndex, isCorrect, selectedLetter }) => (
            <li key={questionIndex}>
              <strong>Question {questionIndex + 1}:</strong>
              {isCorrect ? (
                <span style={{ color: 'green' }}>Correct! Your answer: {selectedLetter}</span>
              ) : (
                <span style={{ color: 'red' }}>Incorrect! Your answer: {selectedLetter}</span>
              )}
            </li>
          ))}
        </ul>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];

  return (
    <div className="quiz-container">
      <div className="quiz-content">
        <div className="question-area">
          <h3 className="question-text">{currentQuestion.question}</h3>
          <div className="options">
            {currentQuestion.options.map((option, index) => (
              <label key={index} className="option-label">
                <input
                  type="radio"
                  name="option"
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => setSelectedOption(option)}
                />
                {option}
              </label>
            ))}
          </div>
          <button
            className="submit-button"
            onClick={handleSubmit}
            disabled={!selectedOption}
          >
            Submit
          </button>

          {/* Inline feedback section */}
          {answerSubmitted && (
            <div className="inline-feedback">
              {feedback[currentIndex].isCorrect ? (
                <span style={{ color: 'green' }}>Correct!</span>
              ) : (
                <span style={{ color: 'red' }}>Incorrect! The correct answer is: {questionsWithAnswers[currentIndex].correctAnswer}</span>
              )}
            </div>
          )}
          
          {/* Next question button */}
          {answerSubmitted && currentIndex < questions.length - 1 && (
            <button
              className="next-button"
              onClick={handleNextQuestion}
            >
              Next Question
            </button>
          )}
        </div>

        <div className="questions-ladder">
          <ol>
            {questions.map((_, index) => (
              <li
                key={index}
                className={`ladder-item ${index === currentIndex ? 'current-question' : ''}`}
              >
                Question {index + 1}
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};

export default Quiz;
