import React, { useEffect, useRef, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom'; // For navigation

const Results = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { score, feedback, totalQuestions } = location.state || {};
  const audioRef = useRef(null);
  const [isMusicPlaying, setIsMusicPlaying] = useState(true);

  useEffect(() => {
    const selectedMusic = localStorage.getItem('selectedMusic');
    let audio = null;
    if (selectedMusic === "Carnatic music") {
      audio = '/assets/music/Carnatic.mp3';
    } else if (selectedMusic === "Hindustani classical music") {
      audio = '/assets/music/Hindustani.mp3';
    } else {
      audio = '/assets/music/generalMusic.mp3';
    }
    audioRef.current = new Audio(audio);
    audioRef.current.play();

    // Cleanup: stop the music when the component unmounts
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.currentTime = 0;
      }
    };
  }, []);

  const handleRestart = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
    navigate('/');
  };

  const toggleMusic = () => {
    if (audioRef.current) {
      if (isMusicPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsMusicPlaying(!isMusicPlaying);
    }
  };

  return (
    <div className="results bg-gradient-to-r from-indigo-600 to-purple-700 min-h-screen text-white flex flex-col items-center justify-center py-12 px-6">
      <h1 className="text-6xl font-extrabold mb-8 text-center animate__animated animate__fadeIn">ðŸŽ‰ Congratulations! ðŸŽ‰</h1>
      <p className="text-3xl mb-6 text-center">You earned <span className="text-yellow-400 font-bold">{score}</span> chocolates out of {totalQuestions}!</p>
      
      <div className="fireworks text-5xl mb-8 animate__animated animate__fadeInUp">
        ðŸŽ† ðŸŽ‡ ðŸŽ† ðŸŽ‡
      </div>
      
     

      <div className="flex justify-center space-x-4 mb-8">
        <button
          onClick={handleRestart}
          className="bg-yellow-500 text-black py-3 px-8 rounded-lg font-semibold shadow-md hover:bg-yellow-400 transition duration-300 transform hover:scale-105"
        >
          Restart Quiz
        </button>
        <button
          onClick={toggleMusic}
          className="bg-gray-800 text-white py-3 px-8 rounded-lg font-semibold shadow-md hover:bg-gray-700 transition duration-300 transform hover:scale-105"
        >
          {isMusicPlaying ? 'Pause Music' : 'Play Music'}
        </button>
      </div>
    </div>
  );
};

export default Results;
