import React, { useEffect, useState } from 'react';

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [questionsWithAnswers, setQuestionsWithAnswers] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const selectedMusic = localStorage.getItem('selectedMusic');

        const response = await fetch('http://localhost:5000/api/v1/questions/generate', {
          method: 'POST', // Specify the HTTP method as POST
          headers: {
            'Content-Type': 'application/json', // Specify that you're sending JSON data
          },
          body: JSON.stringify({
            topic: selectedMusic,
            difficulty: 'easy',
            count: 5, // Include any additional parameters required by your API
          }),
        });
        console.log("response >>>>>>>>>>>>>>> " + response);
        const data = await response.json();
        const questionsArray = [data]; // Convert the object to an array
        setQuestionsWithAnswers(questionsArray);
        const forUser = questionsArray.map(({ correctAnswer, ...rest }) => rest); // Remove correctAnswer for display
        setQuestions(forUser);
      } catch (err) {
        console.error('Error fetching questions:', err);
      }
    };

    fetchQuestions();
  }, []);

  const handleSubmit = () => {
    const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer;
    // Extract selected letter from the selected option (e.g., "A) Michael Jackson" -> "A")
    const selectedLetter = selectedOption.split(')')[0];

    if (correctAnswer === selectedLetter) {
      setScore(score + 1);
    }

    setSelectedOption(null);
    setCurrentIndex(currentIndex + 1);
  };

  if (questions.length === 0) {
    return <div>Loading questions...</div>;
  }

  if (currentIndex >= questions.length) {
    return <div>Your final score is: {score}</div>;
  }

  const currentQuestion = questions[currentIndex];

  return (
    <div>
      <h3>{currentQuestion.question}</h3>
      <div>
        {currentQuestion.options.map((option, index) => (
          <label key={index}>
            <input
              type="radio"
              name="option"
              value={option}
              onChange={() => setSelectedOption(option)}
            />
            {option}
          </label>
        ))}
      </div>
      <button onClick={handleSubmit} disabled={!selectedOption}>
        Submit
      </button>
    </div>
  );
};

export default Quiz;
