import React, { useEffect, useRef, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom'; // For navigation

const Results = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { score, feedback, totalQuestions } = location.state || {};
  const audioRef = useRef(null);
  const [isMusicPlaying, setIsMusicPlaying] = useState(true);

  useEffect(() => {
    const selectedMusic = localStorage.getItem('selectedMusic');
    let audio = null;
    if (selectedMusic === "Carnatic music") {
      audio = '/assets/music/Carnatic.mp3';
    } else if (selectedMusic === "Hindustani classical music") {
      audio = '/assets/music/Hindustani.mp3';
    } else {
      audio = '/assets/music/generalMusic.mp3';
    }
    audioRef.current = new Audio(audio);
    audioRef.current.play();

    // Cleanup: stop the music when the component unmounts
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.currentTime = 0;
      }
    };
  }, []);

  const handleRestart = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
    navigate('/');
  };

  const toggleMusic = () => {
    if (audioRef.current) {
      if (isMusicPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsMusicPlaying(!isMusicPlaying);
    }
  };

  return (
    <div className="results bg-gradient-to-r from-blue-500 to-teal-400 min-h-screen text-white flex flex-col items-center justify-center py-8 px-4">
      <h1 className="text-5xl font-bold mb-6 animate__animated animate__fadeIn">üéâ Congratulations! üéâ</h1>
      <p className="text-2xl mb-4">You earned <span className="text-yellow-400">{score}</span> chocolates out of {totalQuestions}!</p>
      <div className="fireworks mb-4 animate__animated animate__fadeInUp">
        üéÜ üéá üéÜ üéá
      </div>
      
      <h4 className="text-xl font-semibold mb-2">Feedback:</h4>
      <ul className="w-full max-w-md space-y-4 mb-6">
        {feedback.map(({ questionIndex, isCorrect, selectedLetter }) => (
          <li key={questionIndex} className="bg-gray-800 p-4 rounded-lg shadow-lg flex justify-between">
            <div>
              <strong className="text-lg">Question {questionIndex + 1}:</strong>
              <p className={`mt-2 text-sm ${isCorrect ? 'text-green-400' : 'text-red-400'}`}>
                {isCorrect ? `Correct! Your answer: ${selectedLetter}` : `Incorrect! Your answer: ${selectedLetter}`}
              </p>
            </div>
            <div>
              {isCorrect ? (
                <span className="text-green-400 text-xl">‚úîÔ∏è</span>
              ) : (
                <span className="text-red-400 text-xl">‚ùå</span>
              )}
            </div>
          </li>
        ))}
      </ul>

      <button
        onClick={handleRestart}
        className="bg-yellow-500 text-black py-3 px-6 rounded-lg font-semibold hover:bg-yellow-400 transition duration-300 mb-4"
      >
        Restart Quiz
      </button>
      
      <button
        onClick={toggleMusic}
        className="bg-gray-700 text-white py-2 px-5 rounded-lg font-semibold hover:bg-gray-600 transition duration-300"
      >
        {isMusicPlaying ? 'Pause Music' : 'Play Music'}
      </button>
    </div>
  );
};

export default Results;
