import React, { useEffect, useState } from 'react';

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/questions?topic=General%20Knowledge&difficulty=medium&count=5');
        const data = await response.json();
        setQuestions(data);
      } catch (err) {
        console.error('Error fetching questions:', err);
      }
    };

    fetchQuestions();
  }, []);

  const handleSubmit = () => {
    if (questions[currentIndex].correctAnswer === selectedOption) {
      setScore(score + 1);
    }
    setSelectedOption(null);
    setCurrentIndex(currentIndex + 1);
  };

  if (questions.length === 0) {
    return <div>Loading questions...</div>;
  }

  if (currentIndex >= questions.length) {
    return <div>Your final score is: {score}</div>;
  }

  const currentQuestion = questions[currentIndex];

  return (
    <div>
      <h3>{currentQuestion.question}</h3>
      <div>
        {currentQuestion.options.map((option, index) => (
          <label key={index}>
            <input
              type="radio"
              name="option"
              value={option}
              onChange={() => setSelectedOption(option)}
            />
            {option}
          </label>
        ))}
      </div>
      <button onClick={handleSubmit} disabled={!selectedOption}>
        Submit
      </button>
    </div>
  );
};

export default Quiz;
