import React, { useRef, useState, useEffect } from 'react';

const Quiz = () => {
  const audioRef = useRef(null);
  const [volume, setVolume] = useState(0.5);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [isQuizOver, setIsQuizOver] = useState(false);

  // Load music and set volume
  useEffect(() => {
    const selectedMusic = localStorage.getItem('selectedMusic') || 'karnatic';
    audioRef.current.src = `/sounds/${selectedMusic}-music.mp3`;
    audioRef.current.volume = volume;
  }, [volume]);

  // Fetch questions from the backend API
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/questions');
        const data = await response.json();
        setQuestions(data);
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    };

    fetchQuestions();
  }, []);

  // Handle volume adjustment
  const adjustVolume = (event) => {
    setVolume(event.target.value);
  };

  // Handle answer selection
  const handleAnswerSelection = (answer) => {
    setSelectedAnswer(answer);
  };

  // Submit the answer and validate
  const submitAnswer = () => {
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedAnswer === currentQuestion.correctAnswer) {
      setScore((prevScore) => prevScore + currentQuestion.reward);
      setFeedback('Correct! üéâ');
    } else {
      setFeedback(`Wrong! The correct answer was: ${currentQuestion.correctAnswer}`);
    }

    // Move to the next question or end the quiz
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    } else {
      setIsQuizOver(true);
    }

    setSelectedAnswer(null);
  };

  // Render quiz questions or the final score
  return (
    <div className="quiz">
      <h2>Welcome to the Quiz</h2>
      <audio ref={audioRef} loop autoPlay />
      <label className="block mt-4">
        Adjust Volume:
        <input
          type="range"
          min="0"
          max="1"
          step="0.1"
          value={volume}
          onChange={adjustVolume}
          className="mt-2"
        />
      </label>

      {isQuizOver ? (
        <div className="final-score">
          <h3>Quiz Over!</h3>
          <p>Your total chocolate rewards: {score} üç´</p>
        </div>
      ) : (
        <>
          {questions.length > 0 ? (
            <div className="question-section">
              <h3>{questions[currentQuestionIndex].question}</h3>
              <div className="options">
                {questions[currentQuestionIndex].options.map((option, index) => (
                  <button
                    key={index}
                    className={`option ${
                      selectedAnswer === option ? 'selected' : ''
                    }`}
                    onClick={() => handleAnswerSelection(option)}
                  >
                    {option}
                  </button>
                ))}
              </div>
              <button
                onClick={submitAnswer}
                disabled={!selectedAnswer}
                className="submit-btn mt-4"
              >
                Submit Answer
              </button>
              {feedback && <p className="feedback mt-2">{feedback}</p>}
            </div>
          ) : (
            <p>Loading questions...</p>
          )}
        </>
      )}
    </div>
  );
};

export default Quiz;
