import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom'; // Add this import for navigation

const Results = ({ score }) => {
  const navigate = useNavigate(); // Initialize navigate function for routing
  const audioRef = useRef(null);  // Create a ref to store the audio instance

  useEffect(() => {
    // Initialize the audio instance and play the music
    audioRef.current = new Audio('/assets/music/sample.mp3');
    audioRef.current.play();

    // Cleanup: stop the music when the component unmounts
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.currentTime = 0; // Reset the audio to start from the beginning
      }
    };
  }, []);

  const handleRestart = () => {
    // Pause the audio and reset it when restarting
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0; // Reset the audio to start from the beginning
    }
    // Navigate to the quiz start page (or home page)
    navigate('/');
  };

  return (
    <div className="results">
      <h1 className="text-4xl font-bold">Congratulations!</h1>
      <p className="text-2xl">You earned {score} chocolates!</p>
      <div className="fireworks">
