import React, { useEffect, useRef, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom'; // For navigation

const Results = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { score, totalQuestions } = location.state || {};
  const audioRef = useRef(null);
  const [isMusicPlaying, setIsMusicPlaying] = useState(true);
  const [scorePercentage, setScorePercentage] = useState(0);

  useEffect(() => {
    const selectedMusic = localStorage.getItem('selectedMusic');
    let audio = null;
    if (selectedMusic === "Carnatic music") {
      audio = '/assets/music/Carnatic.mp3';
    } else if (selectedMusic === "Hindustani classical music") {
      audio = '/assets/music/Hindustani.mp3';
    } else {
      audio = '/assets/music/generalMusic.mp3';
    }
    audioRef.current = new Audio(audio);
    audioRef.current.play();

    // Cleanup: stop the music when the component unmounts
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.currentTime = 0;
      }
    };
  }, []);

  useEffect(() => {
    const percentage = (score / totalQuestions) * 100;
    setScorePercentage(percentage);
  }, [score, totalQuestions]);

  const handleRestart = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
    navigate('/');
  };

  const toggleMusic = () => {
    if (audioRef.current) {
      if (isMusicPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsMusicPlaying(!isMusicPlaying);
    }
  };

  return (
    <div className="results bg-gradient-to-r from-indigo-600 to-purple-700 min-h-screen text-white flex flex-col items-center justify-center py-16 px-6">
      <h1 className="text-6xl font-extrabold mb-8 text-center animate__animated animate__fadeIn">ðŸŽ‰ Congratulations! ðŸŽ‰</h1>
      <p className="text-3xl mb-10 text-center">You earned <span className="text-yellow-400 font-bold">{score}</span> chocolates out of {totalQuestions}!</p>

      {/* Score Indicator */}
      <div className="flex flex-col items-center justify-center mb-12">
      <p className="text-2xl font-semibold">Score Achieved</p>
        <div className="relative w-56 h-56 mb-4">
          <div className="absolute inset-0 flex justify-center items-center">
            <div className="text-3xl font-bold">{scorePercentage.toFixed(0)}%</div>
          </div>
          <svg width="100%" height="100%" viewBox="0 0 36 36" className="circle-chart">
            <circle className="circle-background" cx="18" cy="18" r="15.915" stroke="#eee" strokeWidth="2" fill="none" />
            <circle
              className="circle-progress"
              cx="18"
              cy="18"
              r="15.915"
              stroke="#4CAF50"
              strokeWidth="2"
              fill="none"
              strokeDasharray={`${scorePercentage} 100`}
              strokeLinecap="round"
            />
          </svg>
        </div>
        
      </div>

      <div className="fireworks text-5xl mb-12 animate__animated animate__fadeInUp">
        ðŸŽ† ðŸŽ‡ ðŸŽ† ðŸŽ‡
      </div>

      &nbsp; &nbsp;

      <div className="flex justify-center space-x-8 mb-12">
        <button
          onClick={handleRestart}
          className="bg-yellow-500 text-black py-4 px-10 rounded-lg font-semibold shadow-lg hover:bg-yellow-400 transition duration-300 transform hover:scale-105"
        >
          Restart Quiz
        </button>
        <button
          onClick={toggleMusic}
          className="bg-gray-800 text-white py-4 px-10 rounded-lg font-semibold shadow-lg hover:bg-gray-700 transition duration-300 transform hover:scale-105"
        >
          {isMusicPlaying ? 'Pause Music' : 'Play Music'}
        </button>
      </div>
    </div>
  );
};

export default Results;
