import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Quiz.css';

const Quiz = () => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [questionsWithAnswers, setQuestionsWithAnswers] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState([]);
  const [answerSubmitted, setAnswerSubmitted] = useState(false);
  const [used50_50, setUsed50_50] = useState(false); // Track if 50-50 is used
  const [usedAudiencePoll, setUsedAudiencePoll] = useState(false); // Track if Audience Poll is used
  const [usedPhoneAFriend, setUsedPhoneAFriend] = useState(false); // Track if Phone A Friend is used
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(false); // To disable the submit button
  const [areLifelinesDisabled, setAreLifelinesDisabled] = useState(false); // To disable lifeline options


  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        
        const selectedMusic = localStorage.getItem('selectedMusic');
        const response = await fetch('http://localhost:5000/api/v1/questions/generate', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            topic: selectedMusic,
            difficulty: 'basic',
            questionsCount: 10,
          }),
        });
        const data = await response.json();
        const questionsArray = data.questions;
        setQuestionsWithAnswers(questionsArray);
        const forUser = questionsArray.map(({ correctAnswer, ...rest }) => rest);
        setQuestions(forUser);
      } catch (err) {
        console.error('Error fetching questions:', err);
      }
    };

    fetchQuestions();
  }, []);

    // Function to trigger text-to-speech for the current question and options
    const readQuestionAndOptionsOutLoud = () => {
      const currentQuestion = questions[currentIndex];
      const questionText = currentQuestion.question;
      const options = currentQuestion.options.join(', ');  // Join options with commas for easier reading
      const utterance = new SpeechSynthesisUtterance(`${questionText} Options are: ${options}`);
      window.speechSynthesis.speak(utterance);
    };
  
    useEffect(() => {
      // Read question and options when the current question changes
      if (questions.length > 0) {
        readQuestionAndOptionsOutLoud();
      }
      const handleBeforeUnload = () => {
        // Stop speech synthesis when leaving the page
        window.speechSynthesis.cancel();
      };
      window.addEventListener('beforeunload', handleBeforeUnload);


      return () => {
        // Clean up and stop any ongoing speech synthesis when leaving the page
        window.speechSynthesis.cancel();
      };
    }, [currentIndex, questions]);  // Re-run when the currentIndex or questions changes

  // Function to check if an option is correct
  const isCorrect = (option) => {
    const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer.trim().toUpperCase();
    const selectedLetter = option.split(')')[0].trim().toUpperCase();
    const correctLetter = correctAnswer.replace(/^.*:\s*/, '').trim();
    return selectedLetter === correctLetter;
  };

  const handleSubmit = () => {
    // Stop the speech synthesis when the submit button is clicked
    window.speechSynthesis.cancel();

    const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer.trim().toUpperCase();
    const selectedLetter = selectedOption.split(')')[0].trim().toUpperCase();
    const correctLetter = correctAnswer.replace(/^.*:\s*/, '').trim();

    const isOptionCorrect = correctLetter === selectedLetter;

    if (isOptionCorrect) {
      setScore(score + 1);
    }

    setFeedback([
      ...feedback,
      { questionIndex: currentIndex, isCorrect: isOptionCorrect, selectedLetter },
    ]);

    setAnswerSubmitted(true);
    setIsSubmitDisabled(true);
    setAreLifelinesDisabled(true);
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setAnswerSubmitted(false);
    setCurrentIndex(currentIndex + 1);
    setIsSubmitDisabled(false);
    setAreLifelinesDisabled(false);
  };

  const handleComplete = () => {
    navigate('/results', {
      state: {
        score,
        feedback,
        totalQuestions: questions.length,
      },
    });
  };

  // 50-50 Lifeline
  const handle50_50 = () => {
    const currentQuestion = questionsWithAnswers[currentIndex];
    
    // Extract the correct option letter from the 'correctAnswer' string (e.g., "C" from "Correct answer: C")
    const correctOptionLetter = currentQuestion.correctAnswer.split(":")[1].trim(); 
  
    // Find the correct option label (e.g., "C) Kriti")
    const correctOption = currentQuestion.options.find(option => option.startsWith(`${correctOptionLetter})`));
  
    // Filter out the correct option from the available options
    const incorrectOptions = currentQuestion.options.filter(option => option !== correctOption);
  
    // Pick one random incorrect option
    const randomIncorrectOption = incorrectOptions[Math.floor(Math.random() * incorrectOptions.length)];
  
    // Set the used50_50 flag to true
    setUsed50_50(true);
  
    // Update the question options to show only the correct option and one random incorrect option
    setQuestions(prevQuestions => {
      const updatedQuestions = [...prevQuestions];
      updatedQuestions[currentIndex] = {
        ...currentQuestion,
        options: [correctOption, randomIncorrectOption], // Only show the correct option and one random incorrect option
      };
      return updatedQuestions;
    });
  };
  
  const handleAudiencePoll = () => {
    setUsedAudiencePoll(true);
  
    // Get the current question
    const currentQuestion = questionsWithAnswers[currentIndex];
  
    console.log("currentQuestion >>>>> ", JSON.stringify(currentQuestion, null, 2));
  
    // Extract correct option identifier from "Correct answer: X" (e.g., "B")
    const correctAnswerIdentifier = currentQuestion.correctAnswer.split(":")[1].trim();
  
    console.log("CorrectAnswerIdentifier >>>>> ", correctAnswerIdentifier);
  
    // Map options to get the option identifier (e.g., A, B, C, D)
    const optionsIdentifiers = ["A", "B", "C", "D"];
  
    // Simulate audience poll results
    const pollResults = optionsIdentifiers.map((identifier, index) => {
      if (identifier === correctAnswerIdentifier) {
        return Math.floor(Math.random() * 50) + 50;  // Give a higher percentage to the correct answer (50-100%)
      } else {
        return Math.floor(Math.random() * 50);  // Random percentage for incorrect options (0-50%)
      }
    });
  
    // Ensure the sum of all options' percentages is 100 by scaling
    const totalPercentage = pollResults.reduce((acc, percentage) => acc + percentage, 0);
    const scale = 100 / totalPercentage;
  
    // Scale the results to ensure they add up to 100
    const scaledPollResults = pollResults.map(percentage => Math.floor(percentage * scale));
  
    console.log("Scaled Poll Results >>>>> ", scaledPollResults);
  
    // Display the audience poll results, marking the correct answer
    alert(`Audience Poll Results:
      A: ${scaledPollResults[0]}% 
      B: ${scaledPollResults[1]}% 
      C: ${scaledPollResults[2]}% 
      D: ${scaledPollResults[3]}% `);
  };
  
  

// Phone A Friend Lifeline
const handlePhoneAFriend = () => {
  setUsedPhoneAFriend(true);
  
  const currentQuestion = questionsWithAnswers[currentIndex];
  
  // Simulate a 50% chance for the correct answer or a wrong answer
  const isCorrect = Math.random() > 0.5; // 50% chance of correct or incorrect
  
  // Simulate the phone-a-friend response
  //alert(`Phone a Friend: The answer is ${isCorrect ? currentQuestion.correctAnswer : "wrong, but might be " + currentQuestion.options[Math.floor(Math.random() * 3)]}`);
  
  // Exclude the correct answer from the phone-a-friend options
  const optionsWithoutCorrect = currentQuestion.options.filter(option => option !== currentQuestion.correctAnswer);
  
  // Simulate the phone-a-friend suggesting a random wrong answer
  alert(`Phone a Friend: The friend might suggest: ${optionsWithoutCorrect[Math.floor(Math.random() * optionsWithoutCorrect.length)]}`);
};

  

  if (questions.length === 0) {
    return (
      <div className="loading">
        <div className="loading-spinner"></div>
        <div className="loading-text">
          Loading Questions
          <span className="dot">.</span>
          <span className="dot">.</span>
          <span className="dot">.</span>
        </div>
      </div>
    );
  }

  const isLastQuestion = currentIndex === questions.length - 1;
  const currentQuestion = questions[currentIndex];

  return (
    <div className="quiz-container">
      <div className="quiz-content">
        <div className="question-area">
          <h3 className="question-text">{currentQuestion.question}</h3>
          <div className="options">
            {currentQuestion.options.map((option, index) => (
              <label 
                key={index} 
                className={`option-label 
                  ${selectedOption === option && isCorrect(option) ? 'correct' : ''} 
                  ${selectedOption === option && !isCorrect(option) ? 'incorrect' : ''} 
                   ${selectedOption ? 'disabled' : ''}`}
              >
                <input
                  type="radio"
                  name="option"
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => setSelectedOption(option)}
                  disabled={selectedOption}  // Disable options after one is selected
                />
                {option}
              </label>
            ))}
          </div>

          <button
            className={`submit-button ${selectedOption === null ? 'disabled' : ''}`}
            onClick={handleSubmit}
            disabled={isSubmitDisabled || selectedOption === null}
          >
            Submit
          </button>

          {/* Inline feedback section */}
          {answerSubmitted && feedback[currentIndex] && (
            <div className="inline-feedback">
              {feedback[currentIndex].isCorrect ? (
                <span style={{ color: 'green' }}>Correct!</span>
              ) : (
                <span style={{ color: 'red' }}>
                {(() => {
                  // Extract the correct answer from the data
                  const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer.replace("Correct answer: ", "");
                  return `Incorrect! The correct answer is: ${correctAnswer}`;
                })()}
                </span>
              )}
            </div>
          )}

          {/* Lifeline Buttons */}
          <div className="lifeline-container">
            {!answerSubmitted && !used50_50 && !areLifelinesDisabled && (
              <button className="lifeline-button" onClick={handle50_50}>
                <img src="/assets/images/50-50.jpg" alt="50-50" />
              </button>
            )}
            {!answerSubmitted && !usedAudiencePoll && !areLifelinesDisabled && (
              <button className="lifeline-button" onClick={handleAudiencePoll}>
                <img src="/assets/images/audiance_poll.jpg" alt="Audience Poll" />
              </button>
            )}
            {!answerSubmitted && !usedPhoneAFriend && !areLifelinesDisabled  && (
              <button className="lifeline-button" onClick={handlePhoneAFriend}>
                <img src="/assets/images/phone_a_friend.webp" alt="Phone a Friend" />
              </button>
            )}
          </div>
          
          {/* Next question button */}
          {answerSubmitted && !isLastQuestion && (
            <button className="next-button" onClick={handleNextQuestion}>
              Next Question
            </button>
          )}
          {/* Complete quiz button */}
          {answerSubmitted && isLastQuestion && (
            <button className="complete-button" onClick={handleComplete}>
              Complete Quiz
            </button>
          )}
        </div>

        {/* Right grid displaying list of questions */}
        <div className="questions-ladder">
          <ol>
            {questions.map((_, index) => (
              <li
                key={index}
                className={`ladder-item ${index === currentIndex ? 'current-question' : ''}`}
                //onClick={() => setCurrentIndex(index)} // Allow clicking on a question to jump to it
              >
                Question {index + 1}
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};

export default Quiz;
