import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Quiz.css';

const Quiz = () => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [questionsWithAnswers, setQuestionsWithAnswers] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState([]);
  const [answerSubmitted, setAnswerSubmitted] = useState(false);
  const [used50_50, setUsed50_50] = useState(false); // Track if 50-50 is used
  const [usedAudiencePoll, setUsedAudiencePoll] = useState(false); // Track if Audience Poll is used
  const [usedPhoneAFriend, setUsedPhoneAFriend] = useState(false); // Track if Phone A Friend is used

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const selectedMusic = localStorage.getItem('selectedMusic');
        const response = await fetch('http://localhost:5000/api/v1/questions/generate', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            topic: selectedMusic,
            difficulty: 'basic',
            questionsCount: 10,
          }),
        });
        const data = await response.json();
        const questionsArray = data.questions;
        setQuestionsWithAnswers(questionsArray);
        const forUser = questionsArray.map(({ correctAnswer, ...rest }) => rest);
        setQuestions(forUser);
      } catch (err) {
        console.error('Error fetching questions:', err);
      }
    };

    fetchQuestions();
  }, []);

  // Function to check if an option is correct
  const isCorrect = (option) => {
    const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer.trim().toUpperCase();
    const selectedLetter = option.split(')')[0].trim().toUpperCase();
    const correctLetter = correctAnswer.replace(/^.*:\s*/, '').trim();
    return selectedLetter === correctLetter;
  };

  const handleSubmit = () => {
    const correctAnswer = questionsWithAnswers[currentIndex].correctAnswer.trim().toUpperCase();
    const selectedLetter = selectedOption.split(')')[0].trim().toUpperCase();
    const correctLetter = correctAnswer.replace(/^.*:\s*/, '').trim();

    const isOptionCorrect = correctLetter === selectedLetter;

    if (isOptionCorrect) {
      setScore(score + 1);
    }

    setFeedback([
      ...feedback,
      { questionIndex: currentIndex, isCorrect: isOptionCorrect, selectedLetter },
    ]);

    setAnswerSubmitted(true);
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setAnswerSubmitted(false);
    setCurrentIndex(currentIndex + 1);
  };

  const handleComplete = () => {
    navigate('/results', {
      state: {
        score,
        feedback,
        totalQuestions: questions.length,
      },
    });
  };

  // 50-50 Lifeline
  const handle50_50 = () => {
    const currentQuestion = questionsWithAnswers[currentIndex];
    const incorrectOptions = currentQuestion.options.filter(
      option => option !== currentQuestion.correctAnswer
    );
    const randomIncorrectOption = incorrectOptions[Math.floor(Math.random() * incorrectOptions.length)];
    
    // Remove two incorrect answers
    const remainingOptions = currentQuestion.options.filter(option => option !== randomIncorrectOption);
    setUsed50_50(true);

    // Set the remaining options for the current question
    setQuestions(prevQuestions => {
      const updatedQuestions = [...prevQuestions];
      updatedQuestions[currentIndex] = {
        ...currentQuestion,
        options: [currentQuestion.correctAnswer, randomIncorrectOption], // Only show correct and one incorrect option
      };
      return updatedQuestions;
    });
  };

  // Audience Poll Lifeline
  const handleAudiencePoll = () => {
    setUsedAudiencePoll(true);
    // Simulate random audience polling results
    const pollResults = [10, 40, 30, 20]; // Random distribution for 4 options
    const currentQuestion = questions[currentIndex];
    alert(`Audience Poll Results: 
      A: ${pollResults[0]}%
      B: ${pollResults[1]}%
      C: ${pollResults[2]}%
      D: ${pollResults[3]}%`);
    // Hide the correct answer in the poll
    const optionsWithoutCorrect = currentQuestion.options.filter(option => option !== currentQuestion.correctAnswer);
    alert(`The options excluding the correct answer are: ${optionsWithoutCorrect.join(', ')}`);        
  };

  // Phone A Friend Lifeline
  const handlePhoneAFriend = () => {
    setUsedPhoneAFriend(true);
    const currentQuestion = questionsWithAnswers[currentIndex];
    const isOptionCorrect = Math.random() > 0.5; // 50% chance the answer is correct or incorrect
    alert(`Phone a Friend: The answer is ${isOptionCorrect ? currentQuestion.correctAnswer : "wrong, but might be " + currentQuestion.options[Math.floor(Math.random() * 3)]}`);

    // Hide the correct answer in the phone-a-friend response
    const optionsWithoutCorrect = currentQuestion.options.filter(option => option !== currentQuestion.correctAnswer);
    alert(`Phone a Friend: The friend might suggest: ${optionsWithoutCorrect[Math.floor(Math.random() * optionsWithoutCorrect.length)]}`);
  };

  if (questions.length === 0) {
    return <div className="loading">Loading questions...</div>;
  }

  const isLastQuestion = currentIndex === questions.length - 1;
  const currentQuestion = questions[currentIndex];

  return (
    <div className="quiz-container">
      <div className="quiz-content">
        <div className="question-area">
          <h3 className="question-text">{currentQuestion.question}</h3>
          <div className="options">
            {currentQuestion.options.map((option, index) => (
              <label 
                key={index} 
                className={`option-label 
                  ${selectedOption === option && isCorrect(option) ? 'correct' : ''} 
                  ${selectedOption === option && !isCorrect(option) ? 'incorrect' : ''} 
                  ${selectedOption && option !== selectedOption ? 'disabled' : ''}`}
              >
                <input
                  type="radio"
                  name="option"
                  value={option}
                  checked={selectedOption === option}
                  onChange={() => setSelectedOption(option)}
                  disabled={selectedOption}  // Disable options after one is selected
                />
                {option}
              </label>
            ))}
          </div>

          <button
            className="submit-button"
            onClick={handleSubmit}
            disabled={!selectedOption}
          >
            Submit
          </button>

          {/* Inline feedback section */}
          {answerSubmitted && feedback[currentIndex] && (
            <div className="inline-feedback">
              {feedback[currentIndex].isCorrect ? (
                <span style={{ color: 'green' }}>Correct!</span>
              ) : (
                <span style={{ color: 'red' }}>
                  Incorrect! The correct answer is: {questionsWithAnswers[currentIndex].correctAnswer}
                </span>
              )}
            </div>
          )}

          {/* Lifeline Buttons */}
          <div className="lifeline-container">
            {!answerSubmitted && !used50_50 && (
              <button className="lifeline-button" onClick={handle50_50}>
                <img src="/assets/images/50-50.jpg" alt="50-50" />
              </button>
            )}
            {!answerSubmitted && !usedAudiencePoll && (
              <button className="lifeline-button" onClick={handleAudiencePoll}>
                <img src="/assets/images/audiance_poll.jpg" alt="Audience Poll" />
              </button>
            )}
            {!answerSubmitted && !usedPhoneAFriend && (
              <button className="lifeline-button" onClick={handlePhoneAFriend}>
                <img src="/assets/images/phone_a_friend.webp" alt="Phone a Friend" />
              </button>
            )}
          </div>
          
          {/* Next question button */}
          {answerSubmitted && !isLastQuestion && (
            <button className="next-button" onClick={handleNextQuestion}>
              Next Question
            </button>
          )}
          {/* Complete quiz button */}
          {answerSubmitted && isLastQuestion && (
            <button className="complete-button" onClick={handleComplete}>
              Complete Quiz
            </button>
          )}
        </div>

        {/* Right grid displaying list of questions */}
        <div className="questions-ladder">
          <ol>
            {questions.map((_, index) => (
              <li
                key={index}
                className={`ladder-item ${index === currentIndex ? 'current-question' : ''}`}
                //onClick={() => setCurrentIndex(index)} // Allow clicking on a question to jump to it
              >
                Question {index + 1}
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
};

export default Quiz;
