import React, { useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom'; // For navigation

const Results = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { score, feedback, totalQuestions } = location.state || {};
  const audioRef = useRef(null);

  useEffect(() => {
    const selectedMusic = localStorage.getItem('selectedMusic');
    let audio = null;
    if (selectedMusic === "Carnatic music") {
      audio = '/assets/music/Carnatic.mp3';
    } else if (selectedMusic === "Hindustani classical music") {
      audio = '/assets/music/Hindustani.mp3';
    } else {
      audio = '/assets/music/generalMusic.mp3';
    }
    audioRef.current = new Audio(audio);
    audioRef.current.play();

    // Cleanup: stop the music when the component unmounts
    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.currentTime = 0;
      }
    };
  }, []);

  const handleRestart = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
    navigate('/');
  };

  return (
    <div className="results">
      <h1 className="text-4xl font-bold">Congratulations!</h1>
      <p className="text-2xl">You earned {score} chocolates out of {totalQuestions}!</p>
      <div className="fireworks">ðŸŽ† ðŸŽ‡ ðŸŽ† ðŸŽ‡</div>
      <h4 className="text-xl">Feedback:</h4>
      <ul>
        {feedback.map(({ questionIndex, isCorrect, selectedLetter }) => (
          <li key={questionIndex}>
            <strong>Question {questionIndex + 1}:</strong>
            {isCorrect ? (
              <span style={{ color: 'green' }}>Correct! Your answer: {selectedLetter}</span>
            ) : (
              <span style={{ color: 'red' }}>Incorrect! Your answer: {selectedLetter}</span>
            )}
          </li>
        ))}
      </ul>
      <br />
      <button
        onClick={handleRestart}
        className="bg-blue-500 text-white py-2 px-4 rounded mt-4"
      >
        Restart Quiz
      </button>
    </div>
  );
};

export default Results;
